import "./Register.css"
export default function Register() {
    const navigate = useNavigate();
    const [inputs, setInput] = useState({
        email: "",
        password: "",
    })
    const [errors, setErrors] = useState({})
    const handleInput = (e) => {
        const nameInput = e.target.name;
        const value = e.target.value;
        setInput(state => ({ ...state, [nameInput]: value }))
    }
    function handleSubmit(e) {
        e.preventDefault();
        let errorSubmit = {};
        let flag = true;
        if (inputs.email == "") {
            errorSubmit.email = "Please enter email";
            flag = false;
        }
        if (inputs.password == "") {
            errorSubmit.password = "Please enter your password";
            flag = false;
        }
        if (!flag) {
            setErrors(errorSubmit);
        }
        if (flag) {
            const data = {
                email: inputs.email,
                password: inputs.password
            }
            console.log(data)
            axios.post("http://localhost/projectnew/public/api/member/login", data)
                .then(response => {
                    if (response.data.errors) {
                        setModalVisible(true)
                    } else {
                        console.log(response)
                        var authParents = {}
                        authParents.data = {}
                        // auth.user.auth_token=response.data
                        authParents.data.auth = response.data.member

                        localStorage.setItem("authParents", JSON.stringify(authParents))
                        navigate('/');
                    }
                })
                .catch(function (error) {
                    console.log(error)
                })
        }
    }
    return (
        <div id="register">
            <div className="container">
                <div className="row">
                    <div className="col-sm-6 ta-end register_logo">
                        <img src="http://localhost/BE/public/images/Image8.jpg" alt={8888} />
                    </div>
                    <div className="col-sm-6 register_logo_name">
                        <p className>Flexfit</p>
                    </div>
                    <div className="col-sm-12 center">
                        <div className="register_title">
                            <p>Register</p>
                        </div>
                    </div>
                    <div className="col-sm-12 center">
                        <form action className="register_form">
                            <div className>
                                <p className="register_form_email">Email Address:</p>
                                <input type="text" className="mb-3" placeholder="alex@gmail.com" /><i className="fa-regular fa-envelope" />
                            </div>
                            <div>
                                <p className="register_form_password">Password:</p>
                                <input type="text" className="mb-3" placeholder="Enter your password" /><i className="fa-solid fa-key" />
                            </div>
                            <div>
                                <p className="register_form_confirm_password">Confirm Password:</p>
                                <input type="text" className="mb-4" placeholder="Re-Enter your password" /><i className="fa-solid fa-key" />
                            </div>
                            <div className="register_form_btn">
                                <button className="btn">Register now</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}
